import React, { Component } from "react";
import api from "../../services/api";
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import ErrorOutlineIcon from '@material-ui/icons/ErrorOutline';

export default class Main extends Component {
  state = {
    products: [],
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async () => {
    const response = await api.get("/v2/list?page=1&limit=20​");
    console.log(response.data);
    this.setState({ products: response.data });
  };

  render() {
    return (
      <div className="product-list">
        {this.state.products.map((products) => (
          <GridListTile
            display="flex"
            flexWrap="wrap"
            justifyContent="space-around"
            overflow="hidden"
            backgroundColor="theme.palette.background.paper"
            color="rgba(255, 255, 255, 0.54)"
          >
            <img src={products.download_url} width="500" height="450"><ErrorOutlineIcon /></img>

            <GridListTileBar
              title={products.id}
              subtitle={<span> {products.author}</span>}
              actionIcon={
                <IconButton aria-label={`info about ${products.url}`}>

                </IconButton>
              }
            />
          </GridListTile>
        ))}
      </div>
    );
  }
}
