import React, { Component } from "react";
import api from "../../services/api";
import {
  Button,
  GridList,
  GridListTile,
  GridListTileBar,
  Grid,
  Paper,
} from "@material-ui/core";

export default class Detalhes extends Component {
  state = {
    products: [],
    page: 1,
    productImg: {},
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async (page) => {
    //const response = await api.get(`/v2/list?page=${page}&limit=30`);
    //console.log(page);
    //this.setState({ products: response.data });

    const { id } = this.props.match.params;

    const response = await api.get(`/id/${id}/400`);
    console.log(response.request.responseURL);
    this.setState({ products: response.request });
  };

  render() {
    const { products } = this.state;

    return (
      <div className="product-list">
        {products.map((products) => (
          <article key={products._id}>
            <p>teste</p>
            <img></img>
          </article>
        ))}
        <img src="https://picsum.photos/id/0/400"></img>
        <img src=""></img>
      </div>
    );
  }
}
