import React, { Component } from "react";
import api from "../../services/api";
import {
  Button,
  GridList,
  GridListTile,
  GridListTileBar,
  Grid,
  Paper,
} from "@material-ui/core";

export default class Detalhes extends Component {
  state = {
    products: {},
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async (page = 1) => {
    const response = await api.get(`/v2/list?page=${page}&limit=30`);
    console.log(page);
    this.setState({ products: response.data });
  };

  render() {
    const { products } = this.state;

    return (
        <div className="product-list">
        <GridList cellHeight={180} className="grid">
          {this.state.products.map((products) => (
            <GridListTile key={products.id}>
              <img src={products.download_url} width="200" height="200"></img>
              <GridListTileBar
                title={products.id}
                subtitle={<span> {products.author}</span>}
                actionIcon={
                  <Button className="button">
                    <InfoIcon>
                    <Link to={`/products/${products._id}`}> Acessar</Link>
                    </InfoIcon>
                  </Button>
                }
              />
            </GridListTile>
          ))}
        </GridList>

        <Grid container spacing={1}>
          <Button
            className="pagesleft"
            variant="contained"
            disabled={page === 1}
            onClick={this.prevPage}
          >
            Anterior
          </Button>

          <Button
            className="pagesright"
            variant="contained"
            onClick={this.nextPage}
          >
            Pr√≥xima
          </Button>
        </Grid>
      </div>
    );
  }
}
