import React, { Component } from "react";
import api from "../../services/api";
import {
  Button,
  GridList,
  GridListTile,
  GridListTileBar,
} from "@material-ui/core";
import InfoIcon from "@material-ui/icons/Info";
import { Link } from "react-router-dom";
import "./styles.css";

export default class Main extends Component {
  state = {
    products: [],
    page: [],
    productInfo: {},
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async (page) => {
    const response = await api.get(`/v2/list?page=${page}`);
    console.log(response.data);
    this.setState({ products: response.data });
  };

  prevPage = () => {
    const { page } = this.state;
  };

  nextPage = () => {
    const { page } = this.state;


    // function pages (page){
    //   console.log(page);
    // }

    // page.forEach (page ++)

    // for (var i =1; i =page ; i++){

    // }

    const pageNumber = page ++;

    this.loadProducts(pageNumber);
  };

  render() {
    const { products, page } = this.state;

    return (
      <div className="product-list">
        <GridList cellHeight={180} className="grid">
          {this.state.products.map((products) => (
            <GridListTile>
              <img src={products.download_url} width="200" height="200"></img>
              <GridListTileBar
                title={products.id}
                subtitle={<span> {products.author}</span>}
                actionIcon={
                  <Button className="button">
                    <InfoIcon></InfoIcon>
                  </Button>
                }
              />
            </GridListTile>
          ))}
        </GridList>
        <Button onClick={this.prevPage}>Anterior</Button>
        <Button onClick={this.nextPage}>Pr√≥xima</Button>
      </div>
    );
  }
}
