import React, { Component } from "react";
import api from "../../services/api";
import {Button, GridList, GridListTile, GridListTileBar} from "@material-ui/core";
import InfoIcon from '@material-ui/icons/Info';


export default class Main extends Component {
  state = {
    products: [],
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async () => {
    const response = await api.get("/v2/list?page=1&limit=20​");
    console.log(response.data);
    this.setState({ products: response.data });
  };

  render() {
    return (
      <div className="product-list">
        <GridList cellHeight={180} className="grid">
          {this.state.products.map((products) => (
            <GridListTile>
              <img src={products.download_url} width="200" height="200"></img>
              <GridListTileBar
                title={products.id}
                subtitle={<span> {products.author}</span>}
                actionIcon={
                  <Button variant="contained" color="primary">
                    <InfoIcon></InfoIcon>
                  </Button>
                }
              />


            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }
}
