import React, { Component } from "react";
import api from "../../services/api";
import {
  Button,
  GridList,
  GridListTile,
  GridListTileBar,
  Grid,
  Paper,
} from "@material-ui/core";

export default class Detalhes extends Component {
  state = {
    products: [],
    page: 1,
    productImg:{}
  };

  componentDidMount() {
    this.loadProducts();
  }
  loadProducts = async (page = 1) => {
    const response = await api.get(`/v2/list?page=${page}&limit=30`);
    console.log(page);
    this.setState({ products: response.data });

    // const { id } = this.props.match.params;

    // const response2 = await api.get(`/id/${id}/400/400`);

    // this.setState({ productImg: response2.data });

  }



  render() {
    const { products } = this.state;
    const { page } = this.state;

    return (
      <div className="product-list">
        <GridList cellHeight={180} className="grid">
          {this.state.products.map((products) => (
            <GridListTile key={products.id}>
              <img src={products.download_url} width="200" height="200"></img>
              <GridListTileBar
                title={products.id}
                subtitle={<span> {products.author}</span>}

              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }
}
